import React, { useEffect } from 'react';
import './Live2DWidget.css';

declare global {
  interface Window {
    L2Dwidget: any;
  }
}

const Live2DWidget: React.FC = () => {
  useEffect(() => {
    // Load Live2D widget script
    const script = document.createElement('script');
    script.src = 'https://unpkg.com/live2d-widget@3.1.4/lib/L2Dwidget.min.js';
    script.async = true;
    document.body.appendChild(script);

    script.onload = () => {
      window.L2Dwidget.init({
        model: {
          jsonPath: 'https://unpkg.com/live2d-widget-model-shizuku@1.0.5/assets/shizuku.model.json',
        },
        display: {
          position: 'right',
          width: 150,
          height: 300,
          hOffset: 0,
          vOffset: -20,
        },
        mobile: {
          show: false, // Hide on mobile devices
          scale: 0.5
        },
        react: {
          opacityDefault: 0.8,
          opacityOnHover: 0.2
        }
      });
    };

    // Tip rotation logic
    const tips = [
      "Tip 1: Stay hydrated while working!",
      "Tip 2: Take regular breaks every hour.",
      "Tip 3: Keep your code clean and readable.",
      "Tip 4: Always use version control (like Git).",
      "Tip 5: Learning never stops — keep exploring!"
    ];

    let tipIndex = 0;
    const tipBox = document.getElementById('tip-box');

    function showNextTip() {
      if (tipBox) {
        tipBox.textContent = tips[tipIndex];
        tipIndex = (tipIndex + 1) % tips.length;
      }
    }

    const tipInterval = setInterval(showNextTip, 8000);

    return () => {
      clearInterval(tipInterval);
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div id="tip-box" className="tip-box">
      Hello! I'm your assistant.
    </div>
  );
};

export default Live2DWidget;
