import React, { useEffect, useRef, useState } from 'react';

interface GameObject {
  x: number;
  y: number;
  width: number;
  height: number;
  speed: number;
  health?: number;
}

interface Bullet extends GameObject {
  type: 'player' | 'enemy';
}

const SpaceShooter: React.FC = () => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [gameOver, setGameOver] = useState(false);
  const [score, setScore] = useState(0);
  const [player, setPlayer] = useState<GameObject>({
    x: 0,
    y: 0,
    width: 40,
    height: 40,
    speed: 5,
    health: 100
  });
  const [bullets, setBullets] = useState<Bullet[]>([]);
  const [enemies, setEnemies] = useState<GameObject[]>([]);
  const [keys, setKeys] = useState<{ [key: string]: boolean }>({});
  const [lastShot, setLastShot] = useState(0);
  const [gameStarted, setGameStarted] = useState(false);

  // Load images
  const [images, setImages] = useState<{
    player?: HTMLImageElement;
    enemy?: HTMLImageElement;
    bullet?: HTMLImageElement;
    background?: HTMLImageElement;
  }>({});

  useEffect(() => {
    // Load game assets
    const loadImages = async () => {
      const playerImg = new Image();
      playerImg.src = 'data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iNDAiIGhlaWdodD0iNDAiIHZpZXdCb3g9IjAgMCA0MCA0MCIgZmlsbD0ibm9uZSIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj48cGF0aCBkPSJNMjAgMEwyMCA0MEw0MCAyMEwyMCAwWiIgZmlsbD0iIzAwZmYwMCIvPjwvc3ZnPg==';

      const enemyImg = new Image();
      enemyImg.src = 'data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iMzAiIGhlaWdodD0iMzAiIHZpZXdCb3g9IjAgMCAzMCAzMCIgZmlsbD0ibm9uZSIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj48Y2lyY2xlIGN4PSIxNSIgY3k9IjE1IiByPSIxNSIgZmlsbD0iI2ZmMDAwMCIvPjwvc3ZnPg==';

      const bulletImg = new Image();
      bulletImg.src = 'data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iNCIgaGVpZ2h0PSIxMCIgdmlld0JveD0iMCAwIDQgMTAiIGZpbGw9Im5vbmUiIHhtbG5zPSJodHRwOi8vd3d3LnczLm9yZy8yMDAwL3N2ZyI+PHJlY3Qgd2lkdGg9IjQiIGhlaWdodD0iMTAiIGZpbGw9IiNmZmZmMDAiLz48L3N2Zz4=';

      const backgroundImg = new Image();
      backgroundImg.src = 'data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iMTAiIGhlaWdodD0iMTAiIHZpZXdCb3g9IjAgMCAxMCAxMCIgZmlsbD0ibm9uZSIgeG1sbnM9Imh0dHA6Ly93d3cudzMub3JnLzIwMDAvc3ZnIj48cmVjdCB3aWR0aD0iMTAiIGhlaWdodD0iMTAiIGZpbGw9IiMwMDAwMDAiLz48L3N2Zz4=';

      await Promise.all([
        new Promise(resolve => { playerImg.onload = resolve; }),
        new Promise(resolve => { enemyImg.onload = resolve; }),
        new Promise(resolve => { bulletImg.onload = resolve; }),
        new Promise(resolve => { backgroundImg.onload = resolve; })
      ]);

      setImages({
        player: playerImg,
        enemy: enemyImg,
        bullet: bulletImg,
        background: backgroundImg
      });
    };

    loadImages();
  }, []);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Set canvas size to fit the container
    const container = canvas.parentElement;
    if (container) {
      canvas.width = container.clientWidth - 20;
      canvas.height = 400;
    }

    // Initialize player position
    setPlayer(prev => ({
      ...prev,
      x: canvas.width / 2 - prev.width / 2,
      y: canvas.height - prev.height - 10
    }));

    // Handle keyboard input
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key === ' ' && !gameStarted) {
        setGameStarted(true);
      }
      setKeys(prev => ({ ...prev, [e.key]: true }));
    };

    const handleKeyUp = (e: KeyboardEvent) => {
      setKeys(prev => ({ ...prev, [e.key]: false }));
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    // Game loop
    let animationFrameId: number;
    const gameLoop = () => {
      if (gameOver) return;

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw background
      if (images.background) {
        const pattern = ctx.createPattern(images.background, 'repeat');
        if (pattern) {
          ctx.fillStyle = pattern;
          ctx.fillRect(0, 0, canvas.width, canvas.height);
        }
      }

      if (!gameStarted) {
        // Draw start screen
        ctx.fillStyle = '#ffffff';
        ctx.font = '24px Arial';
        ctx.textAlign = 'center';
        ctx.fillText('Press SPACE to Start', canvas.width / 2, canvas.height / 2);
        ctx.font = '16px Arial';
        ctx.fillText('Use ← → to move, SPACE to shoot', canvas.width / 2, canvas.height / 2 + 30);
        animationFrameId = requestAnimationFrame(gameLoop);
        return;
      }

      // Update player position
      if (keys['ArrowLeft'] && player.x > 0) {
        setPlayer(prev => ({ ...prev, x: prev.x - prev.speed }));
      }
      if (keys['ArrowRight'] && player.x < canvas.width - player.width) {
        setPlayer(prev => ({ ...prev, x: prev.x + prev.speed }));
      }

      // Shoot bullets with cooldown
      const now = Date.now();
      if (keys[' '] && now - lastShot > 200) { // 200ms cooldown
        setLastShot(now);
        setBullets(prev => [...prev, {
          x: player.x + player.width / 2 - 2,
          y: player.y,
          width: 4,
          height: 10,
          speed: 7,
          type: 'player'
        }]);
      }

      // Update bullets
      setBullets(prev => prev
        .map(bullet => ({ ...bullet, y: bullet.y - bullet.speed }))
        .filter(bullet => bullet.y > -bullet.height && bullet.y < canvas.height)
      );

      // Spawn enemies with increasing difficulty
      if (Math.random() < 0.02 + (score / 10000)) {
        setEnemies(prev => [...prev, {
          x: Math.random() * (canvas.width - 30),
          y: -30,
          width: 30,
          height: 30,
          speed: 2 + (score / 1000),
          health: 1
        }]);
      }

      // Update enemies
      setEnemies(prev => prev
        .map(enemy => ({ ...enemy, y: enemy.y + enemy.speed }))
        .filter(enemy => enemy.y < canvas.height)
      );

      // Check collisions
      setEnemies(prev => {
        const newEnemies = prev.filter(enemy => {
          const hit = bullets.some(bullet => {
            if (bullet.type !== 'player') return false;
            return bullet.x < enemy.x + enemy.width &&
                   bullet.x + bullet.width > enemy.x &&
                   bullet.y < enemy.y + enemy.height &&
                   bullet.y + bullet.height > enemy.y;
          });
          if (hit) {
            setScore(prevScore => prevScore + 10);
            setBullets(prevBullets => prevBullets.filter(b => b !== bullets[0]));
          }
          return !hit;
        });
        return newEnemies;
      });

      // Check game over
      setEnemies(prev => {
        const gameOver = prev.some(enemy => {
          return enemy.y + enemy.height > player.y &&
                 enemy.x < player.x + player.width &&
                 enemy.x + enemy.width > player.x;
        });
        if (gameOver) {
          setGameOver(true);
        }
        return prev;
      });

      // Draw player
      if (images.player) {
        ctx.drawImage(images.player, player.x, player.y, player.width, player.height);
      }

      // Draw bullets
      if (images.bullet) {
        bullets.forEach(bullet => {
          ctx.drawImage(images.bullet, bullet.x, bullet.y, bullet.width, bullet.height);
        });
      }

      // Draw enemies
      if (images.enemy) {
        enemies.forEach(enemy => {
          ctx.drawImage(images.enemy, enemy.x, enemy.y, enemy.width, enemy.height);
        });
      }

      // Draw score and health
      ctx.fillStyle = '#ffffff';
      ctx.font = '16px Arial';
      ctx.textAlign = 'left';
      ctx.fillText(`Score: ${score}`, 10, 30);
      ctx.fillText(`Health: ${player.health}`, 10, 50);

      animationFrameId = requestAnimationFrame(gameLoop);
    };

    gameLoop();

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
      cancelAnimationFrame(animationFrameId);
    };
  }, [gameOver, player, bullets, enemies, keys, score, lastShot, gameStarted, images]);

  return (
    <div className="space-shooter">
      <canvas ref={canvasRef} />
      {gameOver && (
        <div className="game-over">
          <h2>Game Over!</h2>
          <p>Final Score: {score}</p>
          <button onClick={() => {
            setGameOver(false);
            setScore(0);
            setBullets([]);
            setEnemies([]);
            setGameStarted(false);
          }}>Play Again</button>
        </div>
      )}
    </div>
  );
};

export default SpaceShooter;
