import React, { useState } from 'react';
import './WriteBlog.css';

interface BlogPost {
  id: number;
  title: string;
  date: string;
  description: string;
  content: string;
  link: string;
}

const WriteBlog: React.FC = () => {
  const [blogPost, setBlogPost] = useState<BlogPost>({
    id: Date.now(),
    title: '',
    date: new Date().toISOString().split('T')[0],
    description: '',
    content: '',
    link: ''
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // Here you would typically save the blog post to a database or file
    // For now, we'll just log it to the console
    console.log('Blog post submitted:', blogPost);
    alert('Blog post saved successfully!');
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setBlogPost(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div className="write-blog-container">
      <div className="terminal-output">
        <div className="command-line">
          <span className="prompt">$</span>
          <span className="command">nano new-blog.txt</span>
        </div>
        <div className="output">
          <h2 className="section-title">Write New Blog Post</h2>
          <form onSubmit={handleSubmit} className="blog-form">
            <div className="form-group">
              <label htmlFor="title">Title:</label>
              <input
                type="text"
                id="title"
                name="title"
                value={blogPost.title}
                onChange={handleChange}
                required
                className="terminal-input"
              />
            </div>

            <div className="form-group">
              <label htmlFor="date">Date:</label>
              <input
                type="date"
                id="date"
                name="date"
                value={blogPost.date}
                onChange={handleChange}
                required
                className="terminal-input"
              />
            </div>

            <div className="form-group">
              <label htmlFor="description">Description:</label>
              <textarea
                id="description"
                name="description"
                value={blogPost.description}
                onChange={handleChange}
                required
                className="terminal-input"
                rows={3}
              />
            </div>

            <div className="form-group">
              <label htmlFor="content">Content:</label>
              <textarea
                id="content"
                name="content"
                value={blogPost.content}
                onChange={handleChange}
                required
                className="terminal-input"
                rows={10}
              />
            </div>

            <div className="form-group">
              <label htmlFor="link">Link (optional):</label>
              <input
                type="url"
                id="link"
                name="link"
                value={blogPost.link}
                onChange={handleChange}
                className="terminal-input"
                placeholder="https://medium.com/@yourusername/..."
              />
            </div>

            <div className="form-actions">
              <button type="submit" className="terminal-button">
                Save Blog Post
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default WriteBlog;
