import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import ReactMarkdown from 'react-markdown';
import './Blog.css';

interface BlogPostProps {
  onClose: () => void;
}

interface BlogPost {
  id: string;
  title: string;
  date: string;
  content: string;
}

const BlogPost: React.FC<BlogPostProps> = ({ onClose }) => {
  const { postId } = useParams<{ postId: string }>();
  const navigate = useNavigate();
  const [post, setPost] = useState<BlogPost | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPost = async () => {
      try {
        const baseUrl = process.env.PUBLIC_URL || '';
        const response = await fetch(`${baseUrl}/blogs/${postId}.md`);
        const content = await response.text();
        const metadataResponse = await fetch(`${baseUrl}/blogs/index.json`);
        const posts = await metadataResponse.json();
        const postData = posts.find((p: BlogPost) => p.id === postId);

        if (postData) {
          setPost({ ...postData, content });
        } else {
          navigate('/blogs');
        }
      } catch (error) {
        console.error('Error fetching blog post:', error);
        navigate('/blogs');
      } finally {
        setLoading(false);
      }
    };

    fetchPost();
  }, [postId, navigate]);

  if (loading) {
    return (
      <div className="blog-page">
        <div className="blog-loading">Loading blog post...</div>
      </div>
    );
  }

  if (!post) {
    return null;
  }

  return (
    <div className="blog-page">
      <div className="blog-header">
        <h1>{post.title}</h1>
        <button className="close-button" onClick={onClose}>
          Ã—
        </button>
      </div>
      <div className="blog-content">
        <div className="blog-post">
          <div className="post-date">{post.date}</div>
          <div className="post-content">
            <ReactMarkdown>{post.content}</ReactMarkdown>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BlogPost;
