import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Blog.css';

interface BlogPost {
  id: string;
  title: string;
  date: string;
}

interface BlogProps {
  onClose: () => void;
}

const Blog: React.FC<BlogProps> = ({ onClose }) => {
  const [posts, setPosts] = useState<BlogPost[]>([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        // For GitHub Pages, we need to handle the base URL differently
        const isDev = process.env.NODE_ENV === 'development';
        const baseUrl = isDev ? '' : '/portfolio-terminal';
        const response = await fetch(`${baseUrl}/blogs/index.json`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setPosts(data);
      } catch (error) {
        console.error('Error fetching blog posts:', error);
        // Fallback to static data if fetch fails
        setPosts([
          {
            id: "welcome",
            title: "Welcome to My Blog",
            date: "2024-03-20"
          },
          {
            id: "getting-started-with-devops",
            title: "Getting Started with DevOps: A Beginner's Guide",
            date: "2024-03-21"
          }
        ]);
      } finally {
        setLoading(false);
      }
    };

    fetchPosts();
  }, []);

  const handlePostClick = (postId: string) => {
    const isDev = process.env.NODE_ENV === 'development';
    const baseUrl = isDev ? '' : '/portfolio-terminal';
    navigate(`${baseUrl}/blogs/${postId}`);
  };

  if (loading) {
    return (
      <div className="blog-page">
        <div className="blog-loading">Loading blog posts...</div>
      </div>
    );
  }

  return (
    <div className="blog-page">
      <div className="blog-header">
        <h1>Yash's Blog</h1>
        <button className="close-button" onClick={onClose}>
          Ã—
        </button>
      </div>
      <div className="blog-content">
        <div className="blog-list">
          <h2>Recent Posts</h2>
          {posts.map(post => (
            <div 
              key={post.id} 
              className="blog-preview"
              onClick={() => handlePostClick(post.id)}
            >
              <h3>{post.title}</h3>
              <div className="post-date">{post.date}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Blog;
