import React, { useState, useEffect } from 'react';
import ReactMarkdown from 'react-markdown';
import './Blog.css';

interface BlogPost {
  id: string;
  title: string;
  date: string;
  content: string;
}

interface BlogProps {
  onClose: () => void;
}

const Blog: React.FC<BlogProps> = ({ onClose }) => {
  const [posts, setPosts] = useState<BlogPost[]>([]);
  const [selectedPost, setSelectedPost] = useState<BlogPost | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch blog posts
    const fetchPosts = async () => {
      try {
        const response = await fetch('/blogs/index.json');
        const data = await response.json();
        setPosts(data);
      } catch (error) {
        console.error('Error fetching blog posts:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchPosts();
  }, []);

  const loadPostContent = async (postId: string) => {
    try {
      const response = await fetch(`/blogs/${postId}.md`);
      const content = await response.text();
      const post = posts.find(p => p.id === postId);
      if (post) {
        setSelectedPost({ ...post, content });
      }
    } catch (error) {
      console.error('Error loading blog post:', error);
    }
  };

  if (loading) {
    return (
      <div className="blog-page">
        <div className="blog-loading">Loading blog posts...</div>
      </div>
    );
  }

  return (
    <div className="blog-page">
      <div className="blog-header">
        <h1>Yash's Blog</h1>
        <button className="close-button" onClick={onClose}>
          ×
        </button>
      </div>
      <div className="blog-content">
        {selectedPost ? (
          <div className="blog-post">
            <button className="back-button" onClick={() => setSelectedPost(null)}>
              ← Back to Posts
            </button>
            <h1>{selectedPost.title}</h1>
            <div className="post-date">{selectedPost.date}</div>
            <div className="post-content">
              <ReactMarkdown>{selectedPost.content}</ReactMarkdown>
            </div>
          </div>
        ) : (
          <div className="blog-list">
            <h2>Recent Posts</h2>
            {posts.map(post => (
              <div 
                key={post.id} 
                className="blog-preview"
                onClick={() => loadPostContent(post.id)}
              >
                <h3>{post.title}</h3>
                <div className="post-date">{post.date}</div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Blog;
